templates.template:
#ifndef ${include_guard_symbol}
#define ${include_guard_symbol}

#include "OOOCode.h"

#define OOOInterface ${file_base}
OOOVirtuals
	OOOVirtual(int, getData);
OOOVirtualsEnd
#undef OOOInterface

#endif

#ifndef ${include_guard_symbol}
#define ${include_guard_symbol}

#include "OOOCode.h"
#include "I${file_base}.h"

#define OOOClass ${file_base}
OOODeclare(int nMyField)
	OOOImplements
		OOOImplement(I${file_base});
	OOOImplementsEnd
	OOOExports
		OOOExport(int, getMyField);
	OOOExportsEnd
OOODeclareEnd
#undef OOOClass

#endif

#include "OOOUnitTestDefines.h"
#include "${file_base}.h"

OOOTest(${file_base})
{
	${file_base} * p${file_base} = OOOConstruct(${file_base}, 5);

	/* Check stuff here */
	OOOCheck(OOOCall(p${file_base}, getMyField) == 5);
	OOOCheck(OOOICall(OOOCast(I${file_base}, p${file_base}), getData) == 5);

	OOODestroy(p${file_base});
}

#include "${file_base}.h"

#define OOOClass ${file_base}

/* Declare any private data fields here */
OOOPrivateData
	int nMyField;
OOOPrivateDataEnd

OOODestructor
{
	/* release any assigned memory here */
}
OOODestructorEnd

/* sample method */
OOOMethod(int, getMyField)
{
	/* return a private field */
	return OOOF(nMyField);
}
OOOMethodEnd

/* another sample method */
OOOMethod(int, getData)
{
	/* make a private method call */
	return OOOC(getMyField);
}
OOOMethodEnd

OOOConstructor(int nMyField)
{
	/* interface mapping*/
#define OOOInterface I${file_base}
	OOOMapVirtuals
		OOOVirtualMapping(getData)
	OOOMapVirtualsEnd
#undef OOOInterface

	/* export mapping */
	OOOMapMethods
		OOOMethodMapping(getMyField)
	OOOMapMethodsEnd

	/* initialise a private field */
	OOOF(nMyField) = nMyField;
}
OOOConstructorEnd

#undef OOOClass
